package java_day4;

public class Java_1_8_Star {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	
	
	
		for(int i=0;i<11;i++){
			for(int j=15;j>0;j--){
				if(i<j){
					System.out.print(" ");
				}else{
					System.out.print("&*");
				}
			}
			
			System.out.println("&");
		}
	}
}
