package java_day6;

public class Java_1_2_함수2 {

	//함수 :약속된 함수(프로그램을 실행하기 위해 약속된함수)
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		printTitle();
		
	}
	//함수 : 코드 중복 제거 , 코드 분할 ,협업시 유용 
	//기능이름 : printTitle
	//기능 : 성적표의 타이틀 출력하는 기능  
	//입력 : 입력으로 받는 값
	//반환값 : 기능을 수행하고 반환하는 값 ()괄호 표시
	public static void printTitle() {
		System.out.println("성적");
		System.out.println("---");
		System.out.println("@@@");
		
	}
	
}
