package java_day6;

public class Java_1_2_함수4 {

	//함수 :약속된 함수(프로그램을 실행하기 위해 약속된함수)
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		sum1();
		System.out.print(" sum1 코드 실행");
		
	
		
	}

	public static void sum1() {
		int sum = 0;
		for (int i = 0; i <= 10; i++) {
			sum += i;
		}
		System.out.print(sum);
	}


		
	
	
}
