package java_day8;

public class 참조형변수 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int[] arr = new int[3];

		// arr : 참조형 변수 (주소값을 저장하는 변수 )
		// java: 배열부터 new 연산자 (메모리요청) , 반환값은 확보된 메모리의 위치값 정보
		// 참조형 변수의 기본값은 : null (아무것도 참조하지않음)

		//변수의 종류
		int a,b; //기본형
		int [ ] c= new int[3];    //참조형
		int [][] d= new int [3][2];//참조형
		String s; //참조형
	}

}
